actions.script:
def setupWorker = context.setupWorker;
def targetRecord;
def isCreate;
def titleProperty = 'ProductEditCatalog'; // forms have their own subtitles
def target;
def submitFormId;
// WARN: "category" is not a real step name to pass to worker, works for these calls only...
if (!setupWorker?.isSuccessDeleteRecordRequest("category") && 
    (context.productCategoryAndAssoc != null || setupWorker?.isEffectiveNewRecordRequest("category"))) {
    targetRecord = "category"
    isCreate = (context.productCategoryAndAssoc == null);
    target = !isCreate ? 'setupUpdateCategory' : 'setupCreateCategory';
    submitFormId = !isCreate ? 'EditCategory' : 'NewCategory'
} else {
    targetRecord = "catalog"
    isCreate = (context.prodCatalogAndStoreAssoc == null);
    if (isCreate) titleProperty = 'ProductNewCatalog';
    target = !isCreate ? 'setupUpdateCatalog' : 'setupCreateCatalog';
    submitFormId = !isCreate ? 'EditCatalog' : 'NewCatalog';
}
context.targetRecord = targetRecord;
context.isCreate = isCreate;
context.titleProperty = titleProperty;
context.target = target;
context.submitFormId = submitFormId;

import org.ofbiz.base.util.*;
ectMaxProductsPerCat = context.ectMaxProductsPerCat;
if (ectMaxProductsPerCat == null) {
    try {
        ectMaxProductsPerCat = parameters.setupEctMaxProductsPerCat as Integer;
    } catch(Exception e) {
    }
    if (ectMaxProductsPerCat != null) {
        session.setAttribute("setupEctMaxProductsPerCat", ectMaxProductsPerCat);
    }
}
if (ectMaxProductsPerCat == null) ectMaxProductsPerCat = 0; // DEFAULT ZERO: fastest and least confusing
context.ectMaxProductsPerCat = ectMaxProductsPerCat;
